import styled from 'styled-components'
import Navbar from '../components/Navbar'
import Announcement from '../components/Announcement'
import Footer from '../components/Footer'

const Container = styled.div`

`

const Wrapper = styled.div`
  padding: 20px;
`

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`

const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  border: ${props=>props.lb === "filled" && "none"};
  background-color: ${props=>props.lb === "filled" ? "black" : "transparent"};
  color: ${props=>props.lb === "filled" && "white"};
`

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
`

const TopTexts = styled.div`

`

const Summary = styled.div`
  flex: 1;
`

const Info = styled.div`
  flex: 3;
`

const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
`

const Product = styled.div`
  display: flex;
  justify-content: space-between;
`

const ProductDetail = styled.div`
  flex: 2;
  display: flex;
`

const Image = styled.img`
  width: 200px;
`

const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`

const Name = styled.span`

`

const IDD = styled.span`

`

const Color = styled.div`
  width: 20px; height: 20px;
  border-radius: 50%;
  background-color: ${props=>props.color};
`

const Size = styled.span`

`

const PriceDetail = styled.div`
  flex: 1;
`

const Cart = () => {
  return (
    <Container>
      <Announcement/>
      <Navbar/>
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag (2)</TopText>
            <TopText>Your Wishlist (0)</TopText>
          </TopTexts>
          <TopButton lb = "filled">CHECKOUT NOW</TopButton>
        </Top>
        <Bottom>
          <Info>
            <Product>
              <ProductDetail>
                <Image src="https://thesneakerhouse.com/wp-content/uploads/2019/08/BOSTON-RED-SOX-SNEAKERS-BIG-BALL-CHUNKY-P.jpg"/>
                <Details>
                  <Name><b>Product:</b> MLJ Jun Boston Shoes</Name>
                  <IDD><b>ID:</b> 20520334</IDD>
                  <Color color="black"></Color>
                  <Size><b>Size:</b> 41.5</Size>
                </Details>
              </ProductDetail>
              <PriceDetail>
                price
              </PriceDetail>
            </Product>
          </Info>
          <Summary>summary</Summary>
        </Bottom>
      </Wrapper>
      <Footer/>
    </Container>
  )
}

export default Cart
